import React from 'react';
import { useState } from "react";
import { useEffect } from "react";
import {Link } from "react-router-dom";

function StudentTable(props) {
  let [students, setStudents] = useState(null);
  const [direction, setDirection] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [data, setData] = useState([]);
  // let [students, setStudents] = useState([]);

  useEffect(() => {
    console.log("student table useEffect!!");
    setStudents(props.data);
  }, [props.data]);

  // const [status, setStatus] = useState("");
  const deleteUser = (id) => {
    fetch("https://62b5894742c6473c4b348ec8.mockapi.io/students"+id, {method: "DELETE",
    }).then(()=> {
      console.log("Delete successfull!");
      let rs = [...students];
      rs = rs.filter((student) => {
        return student.id !== id;
      })
      setStudents = (rs);
    });
  };

  // const deleteItem = (id) => {
  //   setStudents(() => {
  //    const sortData = [...students];
  //     sortData.splice(id,1);
  //
  //   })
  //   }
  // const deleteItem = (id) => () => setStudents((id)=> id)
  const deleteItem = (index) => () => setStudents((items) => items.filter((_, i) => i !== index));

  const sortColumn = (field, type) => {
    
    const sortData = [...students]; //tạo mới dữ liệu để state trỏ đến dữ liệu mới
    //const sortData = students;
    if (type === "string") {
      sortData.sort((a, b) => direction * a[field].localeCompare(b[field]));
    } else if (type === "number") {
      sortData.sort((a, b) => direction * (a[field] - b[field]));
    }
    setDirection(direction * -1);
    setStudents(sortData);
  };

  console.log("student table main");
  var student_list = [];
  if (students != null) {
    student_list = students.map((item, index) => (
      // <tr className={item.style}>
      <tr key={item.id}
        className={
          item.mark >= 9
            ? "table"
            : item.mark > 4
            ? "table"
            : "table"
        }
      >
        <td>{item.id}</td>
        <td>{item.firstName}</td>
        <td>{item.lastName}</td>
        <td>{item.email}</td>
        <td>{item.mark}</td>
        <td><Link to = {`user/${item.id}`}>Detail</Link></td>
        <td><button className="btn btn-outline-danger" onClick={deleteItem(index)}><i className="fa-solid fa-trash"></i></button></td>
      </tr>
    ));
  }

  return (
    <table className="table thead-dark">
      <tr>
        <th>ID</th>
        <th onClick={() => sortColumn("firstName", "string")}>First Name</th>

        <th onClick={() => sortColumn("lastName", "string")}>
          Last Name <span> </span>
          {/*  <a href="?sort=desc">
            <i class="fa fa-arrow-down"></i>
          </a> */}
        </th>
        <th>Email</th>
        <th onClick={() => sortColumn("mark", "number")}>Mark</th>
        <th>Detail</th>
        <th>Action</th>

      </tr>
      {student_list}
    </table>
  );
}


export default StudentTable;
